// group.rs
extern crate rug;
use rug::Integer;
use rug::rand::RandState;

pub struct Group {
    pub n: Integer,
    pub p: Integer,
    pub q: Integer,
    pub n_sq: Integer,
    pub n_root: Integer,
    pub g: Integer,
    pub mu: Integer,
    pub phi_n: Integer,
    pub delta: Integer,
}

impl Group {
    // A constructor method
    pub fn new(bit_len: u64) -> Group {
        let p;
        let q;
        if bit_len == 10 {
            p = Integer::from(107);
            q = Integer::from(83);
        }else if bit_len == 100 {
            p = Integer::from_str_radix("6122421090493547576937037317561418841225758554253106999", 10).unwrap();
            q = Integer::from_str_radix("5846418214406154678836553182979162384198610505601062333", 10).unwrap();
        }else if bit_len == 200 {
            p = Integer::from_str_radix("1326857892134202909120271964903022906368536763514545292972947", 10).unwrap();
            q = Integer::from_str_radix("1338895862330910741957787831565077829159980893619325834591339", 10).unwrap();
        }else if bit_len == 300 {
            p = Integer::from_str_radix("1915984845927576957708467687167321155212629745144461093134087187851839709628979316497251727", 10).unwrap();
            q = Integer::from_str_radix("1612287714961239006025713291392020709541479461928326050152706104567238058087596871531092347", 10).unwrap();
        }else if bit_len == 3072 {
            p = Integer::from_str_radix("", 10).unwrap();
            q = Integer::from_str_radix("", 10).unwrap();
        } else {
            p = Integer::from_str_radix("863", 10).unwrap();
            q = Integer::from_str_radix("839", 10).unwrap();
        }
        
        let n = p.clone() * q.clone();
        let n_sq = n.clone().square();
        let n_root = n.clone().root(2);
        let mut g = n_sq.clone();
        let mut rand = RandState::new();
        g = g.clone().random_below(&mut rand);
        
        
        let phi_n: Integer = (p.clone() - 1) * (q.clone() - 1);
        let delta = n.clone() * phi_n.clone();

        // for lambda = g^phi_n, define L = (lambda-1) / n and mu = L^{-1} mod n
        let lambda = g.clone().pow_mod(&phi_n, &n_sq).unwrap();
        let l: Integer = (lambda.clone() - Integer::from(1)) / n.clone();
        let mu = l.clone().invert(&n).unwrap() * phi_n.clone();

        Group {
            n,
            p,
            q,
            n_sq,
            n_root,
            g,
            mu,
            phi_n,
            delta,
            // ... initialize other fields
        }
    }
}

// implement Group clone
impl Clone for Group {
    fn clone(&self) -> Group {
        Group {
            n: self.n.clone(),
            p: self.p.clone(),
            q: self.q.clone(),
            n_sq: self.n_sq.clone(),
            n_root: self.n_root.clone(),
            g: self.g.clone(),
            mu: self.mu.clone(),
            phi_n: self.phi_n.clone(),
            delta: self.delta.clone(),
        }
    }
}

use std::fmt;

impl fmt::Display for Group {
    fn fmt(&self, f: &mut fmt::Formatter<'_>) -> fmt::Result {
        write!(f, "Group:\n")?;
        write!(f, "    n: {}\n", self.n)?;
        write!(f, "    p: {}\n", self.p)?;
        write!(f, "    q: {}\n", self.q)?;
        write!(f, "    n_sq: {}\n", self.n_sq)?;
        write!(f, "    n_root: {}\n", self.n_root)?;
        write!(f, "    g: {}\n", self.g)?;
        write!(f, "    mu: {}\n", self.mu)?;
        write!(f, "    phi_n: {}\n", self.phi_n)?;
        write!(f, "    delta: {}\n", self.delta)?;
        // ... include other fields you want to display

        Ok(())
    }
}

pub fn discrete_logarithm(x: Integer, grp: &Group) -> Integer {
    let x_m1 = x.clone() - Integer::from(1);
    let (quo, _rem) = x_m1.div_rem_euc(grp.n.clone());
    // (x - 1) / grp.n.clone()
    quo
}